  CREATE OR REPLACE EDITIONABLE PROCEDURE "XXBAF"."SENDINVMAIL" (v_afpno    varchar2,
                                        v_mailFlag varchar2) IS

  V_PROC             XXBAF_ERR_DATA_TRANSFER.ERR_PROC%TYPE;
  V_ERRMSG           XXBAF_ERR_DATA_TRANSFER.ERR_MSG%TYPE;
  V_STATUS           XXBAF_ERR_DATA_TRANSFER.ERR_STATUS%TYPE;
  V_SUPERVISOR       varchar2(100);
  p_user_email       varchar2(500);
  p_supervisor_email varchar2(500);
  p_fin_email        varchar2(500);
  MYDATA             VARCHAR2(32000);
  v_sender_add       varchar2(500);
  v_sub              varchar2(100);
  v_submit_by        varchar2(100);
  v_supplier         varchar2(500);
  v_inv_no           varchar2(300);
begin
  V_STATUS     := 1;
  V_PROC       := 'sendInvMail';
  v_sender_add := 'oracle.ebs@oracleebs.baf.com';

  SELECT ATTRIBUTE1, submitted_by, invoice_no, supplier_name
    INTO V_SUPERVISOR, v_submit_by, v_inv_no, v_supplier
    FROM XXBAF_INVOICE_MSTR
   WHERE AFPNO = v_afpno;

  SELECT user_email
    INTO p_supervisor_email
    FROM xxbaf_user_mstr
   WHERE loginid = V_SUPERVISOR;

  SELECT user_email
    INTO p_user_email
    FROM xxbaf_user_mstr
   WHERE loginid = v_submit_by;

     SELECT user_email
    INTO p_fin_email
    FROM xxbaf_user_mstr
   WHERE USER_ROLE=4
   AND ROWNUM=1;
   
  ------Invoice submiited by user -- mail to supervisor and user
  IF (v_mailFlag = 'P') THEN
    V_STATUS := 2;
    BEGIN
    
      v_sub := 'Invoice ' || v_inv_no || ' Pending For Approval';
    
      MYDATA := 'The Invoice is Pending for your approval ' ||chr(13)||chr(10)||
                ' Please find the below details' ||chr(13)||chr(10)||
                ' AFP NO: ' || v_afpno ||chr(13)||chr(10)||
                'Invoice NO: ' || v_inv_no ||chr(13)||chr(10)||
                'Supplier Name: ' || v_supplier ||chr(13)||chr(10)||
                 chr(13)||chr(10)||
                'Please click the below URL to Approve ' ||chr(13)||chr(10)||
                'URL: http://172.16.100.77:8080/BAFAFP/' ;
    
      baf_mail_send(p_sender     => v_sender_add,
                    p_recipients => p_supervisor_email,
                    p_subject    => v_sub,
                    p_message    => MYDATA);
    
      v_sub := 'Invoice ' || v_inv_no || ' Submitted For Approval';
    
      MYDATA := 'The Invoice has been submitted for approval ' ||chr(13)||chr(10)||
                ' Please find the below details' ||chr(13)||chr(10)||
                ' AFP NO: ' || v_afpno ||chr(13)||chr(10)||
                'Invoice NO: ' || v_inv_no ||chr(13)||chr(10)||
                'Supplier Name: ' || v_supplier;
    
      baf_mail_send(p_sender     => v_sender_add,
                    p_recipients => p_user_email,
                    p_subject    => v_sub,
                    p_message    => MYDATA);
    
    end;
  end if;

------Invoice approved by supervisor----mail to finance and user

  IF (v_mailFlag = 'S') THEN
    V_STATUS := 3;
    BEGIN
      v_sub := 'Invoice ' || v_inv_no || ' Pending For Approval';
    
      MYDATA := 'The Invoice is Pending for your approval ' ||chr(13)||chr(10)||
                'Please find the below details' ||chr(13)||chr(10)||
                'AFP NO: ' || v_afpno ||chr(13)||chr(10)||
                'Invoice NO: ' || v_inv_no ||chr(13)||chr(10)||
                'Supplier Name: ' || v_supplier ||chr(13)||chr(10)||
                 chr(13)||chr(10)||
                'Please click the below URL to Approve ' ||chr(13)||chr(10)||
                'URL: http://172.16.100.77:8080/BAFAFP/' ;
    
      baf_mail_send(p_sender     => v_sender_add,
                    p_recipients => p_fin_email,
                    p_subject    => v_sub,
                    p_message    => MYDATA);
                
                
     v_sub := 'Invoice ' || v_inv_no || ' Submitted For Finance Approval';
    
      MYDATA := 'The Invoice has been submitted for Finance Approval ' ||chr(13)||chr(10)||
                'Please find the below details' ||chr(13)||chr(10)||
                'AFP NO: ' || v_afpno ||chr(13)||chr(10)||
                'Invoice NO: ' || v_inv_no ||chr(13)||chr(10)||
                'Supplier Name: ' || v_supplier;
    
      baf_mail_send(p_sender     => v_sender_add,
                    p_recipients => p_user_email,
                    p_subject    => v_sub,
                    p_message    => MYDATA);           
                
    end;
  end if;

  IF (v_mailFlag = 'A') THEN
    V_STATUS := 4;
    BEGIN
      v_sub := 'Invoice ' || v_inv_no || ' Has Been Approved';
    
      MYDATA := 'The Invoice has been Approved ' ||chr(13)||chr(10)||
                'Please find the below details' ||chr(13)||chr(10)||
                'AFP NO: ' || v_afpno ||chr(13)||chr(10)||
                'Invoice NO: ' || v_inv_no ||chr(13)||chr(10)||
                'Supplier Name: ' || v_supplier;
    
      baf_mail_send(p_sender     => v_sender_add,
                    p_recipients => p_user_email,
                    p_subject    => v_sub,
                    p_message    => MYDATA);
                    
    end;
  
  end if;

  IF (v_mailFlag = 'R') THEN
    V_STATUS := 5;
    BEGIN
      v_sub := 'Invoice ' || v_inv_no || ' Has Been Rejected';
    
      MYDATA := 'The Invoice has been Rejected ' ||chr(13)||chr(10)||
                'Please find the below details' ||chr(13)||chr(10)||
                'AFP NO: ' || v_afpno ||chr(13)||chr(10)||
                'Invoice NO: ' || v_inv_no ||chr(13)||chr(10)||
                'Supplier Name: ' || v_supplier;
    
      baf_mail_send(p_sender     => v_sender_add,
                    p_recipients => p_user_email,
                    p_subject    => v_sub,
                    p_message    => MYDATA);
    end;
  
  end if;

 IF (v_mailFlag = 'I') THEN
    V_STATUS := 4;
    BEGIN
      v_sub := 'Invoice ' || v_inv_no || ' Has Been Saved As Draft';
    
      MYDATA := 'The Invoice has been Saved As Draft ' ||chr(13)||chr(10)||
                'Please find the below details' ||chr(13)||chr(10)||
                'AFP NO: ' || v_afpno ||chr(13)||chr(10)||
                'Invoice NO: ' || v_inv_no ||chr(13)||chr(10)||
                'Supplier Name: ' || v_supplier;
    
      baf_mail_send(p_sender     => v_sender_add,
                    p_recipients => p_user_email,
                    p_subject    => v_sub,
                    p_message    => MYDATA);
                    
    end;
  
  end if;
 
 
 
 
EXCEPTION
  WHEN others THEN
    ROLLBACK;
    V_ERRMSG := SUBSTR(SQLERRM, 1, 250);
    INSERT INTO XXBAF_ERR_DATA_TRANSFER
      (ERR_PROC, ERR_MSG, ERR_DATE, ERR_STATUS)
    VALUES
      (V_PROC, V_ERRMSG, SYSDATE, V_STATUS);
    COMMIT;
end;

