  CREATE OR REPLACE EDITIONABLE PROCEDURE "XXBAF"."XXBAF_AP_INV_LOAD" (P_INV_DETAIL VARCHAR2, P_app VARCHAR2, P_rem VARCHAR2,p_mesg_error out varchar2) AS

  LINE            TABLE_TYPES1.TSTRING;
  FIELD           TABLE_TYPES1.TSTRING;
  V_TERM_ID       NUMBER;
