  l_dev_status           VARCHAR2(80);
  l_message              VARCHAR2(2000);
  l_phase                VARCHAR2(80);
  l_status               VARCHAR2(80);
  l_wait                 BOOLEAN  :=  FALSE;

  l_batch_name           varchar2(200) := 'BAFAFP'||trunc(sysdate);
  l_source_name          varchar2(200) := 'BAFAFP';
  l_org_id               number;
  p_user_id               number;
  p_res_id                number;
  p_res_app_id            number;
  P_INVOICE_ID            NUMBER;
  P_INV_LINE_NUM          NUMBER;
  p_inv_org               number;
  p_loc_id                number;
  p_igst_id               number;
  p_cgst_id               number;
  p_sgst_id               number;
  P_GL_ACCT               VARCHAR2(100);
  P_PREPAY_INV            VARCHAR2(100);
  P_PREPAY_AMT            NUMBER;  
  P_PRE_FLAG              VARCHAR2(100);
  p_inv_amt               number;

  cursor IHDR_CUR(P_AFP_NO NUMBER) IS
 SELECT *
   FROM XXBAF_INVOICE_MSTR
  WHERE STATUS = 'S'
    AND AFPNO = P_AFP_NO
    and ATTRIBUTE2 is NULL;

 cursor IDTL_CUR(P_AFP_NO NUMBER,P_INV_NO VARCHAR2) IS
 SELECT * FROM XXBAF_INVOICE_LINES WHERE AFPNO = P_AFP_NO AND INVOICE_NO = P_INV_NO;
 
 cursor C_INVUPD IS
  SELECT inv.invoice_id, invl.LINE_NUMBER,mst.invoice_no,mst.afpno,mst.supplier_site
   FROM XXBAF_INVOICE_MSTR mst, ap_invoices_all inv, ap_invoice_lines_all invl
  WHERE mst.STATUS = 'A'
    and mst.ATTRIBUTE2 is NULL
    and mst.ATTRIBUTE3 = 'I'
    and inv.invoice_id = invl.invoice_id
    and inv.ATTRIBUTE6 = mst.afpno
    and inv.invoice_num = mst.invoice_no||mst.inv_period
    and inv.vendor_site_id = mst.supplier_site;
    
     cursor C_INVMAIL IS
  SELECT distinct mst.afpno,mst.supplier_site
   FROM XXBAF_INVOICE_MSTR mst, ap_invoices_all inv, ap_invoice_lines_all invl
  WHERE mst.STATUS = 'A'
    and mst.ATTRIBUTE2 is NULL
    and mst.ATTRIBUTE3 = 'S'
    --and inv.invoice_id = invl.invoice_id
    and inv.ATTRIBUTE6 = mst.afpno
    and mst.attribute4 is null
   -- and inv.invoice_num = mst.invoice_no||mst.inv_period
    and inv.vendor_site_id = mst.supplier_site;

BEGIN

SELECT USER_ID
  into p_user_id
  FROM APPS.FND_USER
 WHERE USER_NAME = 'BAFAFP';
 
SELECT responsibility_id, application_id
       into p_res_id,p_res_app_id
  FROM fnd_responsibility_tl
 WHERE responsibility_name = 'BAF Payables SuperUser';
 
      fnd_global.apps_initialize (user_id           => p_user_id,
                                  resp_id           => p_res_id,
                                  resp_appl_id      => p_res_app_id
                                 );
     
      mo_global.init ('SQLAP');
     

LINE := XXBAF_SPLIT1(P_INV_DETAIL, '@@');

FOR I IN 1 .. LINE.COUNT LOOP
V_ERRMSG :=NULL;

FIELD := XXBAF_SPLIT1(LINE(I), '~');

FOR RHDR_CUR IN IHDR_CUR(FIELD(1)) LOOP

  V_TERM_ID :=0;
  V_TERM_NAME := NULL;
  v_voucher_seq   :=0;
  v_voucher_num:= null;
  l_org_id :=RHDR_CUR.ORG_ID;
 P_PREPAY_INV := NULL;
  P_PREPAY_AMT := NULL;
  P_PRE_FLAG := NULL;
  p_inv_amt :=0;
  
   mo_global.set_policy_context ('S', l_org_id);

BEGIN

  select att.term_id, att.name
    INTO V_TERM_ID, V_TERM_NAME
    from APPS.ap_terms_tl           att,
         APPS.ap_suppliers          s,
         APPS.ap_supplier_sites_all ss
   where ss.vendor_id = s.vendor_id
     and ss.terms_id = att.term_id
     and s.SEGMENT1 = RHDR_CUR.SUPPLIER_NO
     and Ss.VENDOR_SITE_ID = RHDR_CUR.SUPPLIER_SITE
     and ss.org_id = RHDR_CUR.ORG_ID;

EXCEPTION
         WHEN OTHERS THEN
        V_TERM_ID :=0;
         V_TERM_NAME := NULL;
END;

IF RHDR_CUR.INVOICE_TYPE = 'STANDARD' THEN
BEGIN

 SELECT AP.INVOICE_NUM, TOT_PREPAY_AMT
   INTO P_PREPAY_INV, P_PREPAY_AMT
   FROM XXBAF.XXBAF_INV_PREDED_AMT PRE, AP_INVOICES_ALL AP
  WHERE PRE.AFPNO = FIELD(1)
    AND PRE.INVOICE_NO = RHDR_CUR.INVOICE_NO
    AND PRE.PREPAYINV_ID = AP.INVOICE_ID;

P_PRE_FLAG := 'Y';
EXCEPTION
         WHEN OTHERS THEN
         P_PREPAY_INV := NULL;
         P_PREPAY_AMT := NULL;
         P_PRE_FLAG := NULL;
END;
ELSE
    P_PREPAY_INV := NULL;
    P_PREPAY_AMT := NULL;
    P_PRE_FLAG := NULL;
END IF;

 select nvl(max(VOUCHER_SEQ) + 1, '1001')
   into v_voucher_seq
   from XXBAF_INVOICE_MSTR
  where trunc(gl_date) = TO_CHAR(TO_DATE(FIELD(2)), 'dd-MON-yyyy');

 select 'AFP/AP/' || v_voucher_seq || '/' ||
        TO_CHAR(TO_DATE(FIELD(2)), 'ddMMyyyy')
   into v_voucher_num
   from dual;


BEGIN
  
BEGIN
BEGIN
  UPDATE XXBAF_INVOICE_MSTR
     SET GL_DATE        = FIELD(2),
         VOUCHER_NUMBER = v_voucher_num,
         VOUCHER_SEQ    = v_voucher_seq,
         STATUS = 'A',
         finance_appr_rej_dt = sysdate,
         finance_appr_rej_by = P_app,
         finance_remarks = P_rem
         
   WHERE AFPNO = FIELD(1);

EXCEPTION
