set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "XXBAF"."XXBAF_FORGOT_PASSWORD" (v_userId       varchar2,
                                                  v_enc_password varchar2,
                                                  v_mail_Flag    varchar2,
                                                  v_status_out   out varchar2,
                                                  v_new_Pass     varchar2) IS

  V_PROC       XXBAF_ERR_DATA_TRANSFER.ERR_PROC%TYPE;
  V_ERRMSG     XXBAF_ERR_DATA_TRANSFER.ERR_MSG%TYPE;
  V_STATUS     XXBAF_ERR_DATA_TRANSFER.ERR_STATUS%TYPE;
  cnt          number;
  v_sender_add varchar2(500);
  v_sub        varchar2(100);
  MYDATA       VARCHAR2(32000);
  p_user_nm    varchar2(200);
  p_user_email varchar2(500);
BEGIN
  v_sender_add := 'oracle.ebs@oracleebs.baf.com';

  V_STATUS := 1;
  V_PROC   := 'xxbaf_forgot_password';

  select count(*)
    into cnt
    from xxbaf_user_mstr
   where uppeR(loginid) = upper(v_userId);

  if (v_mail_Flag = 'Forgot Pass') THEN
    v_sub := 'Forgot Password';
    if (cnt > 0) then
    
      update xxbaf_user_mstr
         set user_pass      = v_enc_password,
             password_flag  = 0,
             login_fail_seq = 0,
             login_fail_dt  = null,
             user_lock_flag = 0,
             user_lock_dt   = null
       where uppeR(loginid) = upper(v_userId);
    
      V_STATUS := 2;
      begin
        select upper(user_name), user_email
          into p_user_nm, p_user_email
          from xxbaf_user_mstr
         where uppeR(loginid) = upper(v_userId);
      
        MYDATA := 'Dear: ' || p_user_nm || chr(13) || chr(10) ||
                  ' Please find the below login details ' || chr(13) ||
                  chr(10) || ' Login Id: ' || v_userId || chr(13) ||
                  chr(10) || ' Password: test12345';
      
        V_STATUS := 3;
      
        baf_mail_send(p_sender     => v_sender_add,
                      p_recipients => p_user_email,
                      p_subject    => v_sub,
                      p_message    => MYDATA);
        v_status_out := 'Y';
      EXCEPTION
        WHEN OTHERS THEN
          ROLLBACK;
          V_ERRMSG := SUBSTR(SQLERRM, 1, 250);
          INSERT INTO XXBAF_ERR_DATA_TRANSFER
            (ERR_PROC, ERR_MSG, ERR_DATE, ERR_STATUS)
          VALUES
            (V_PROC, V_ERRMSG, SYSDATE, V_STATUS);
          COMMIT;
      end;
    
    else
      v_status_out := 'N';
    
    END IF;
  
  end if;

  if (v_mail_Flag = 'Change Pass') THEN
    v_sub := 'Your Password Has Been Changed';
    if (cnt > 0) then
    
      --update xxbaf_user_mstr set user_pass = v_enc_password where uppeR(loginid) = upper(v_userId);
    
      V_STATUS := 2;
      begin
        select upper(user_name), user_email
          into p_user_nm, p_user_email
          from xxbaf_user_mstr
         where uppeR(loginid) = upper(v_userId);
      
        MYDATA := 'Dear: ' || p_user_nm || chr(13) || chr(10) ||
                  ' Please find the below login details ' || chr(13) ||
                  chr(10) || ' Login Id: ' || v_userId; 
                  /*|| chr(13) ||
                  chr(10) || ' Password: ' || v_new_Pass;*/
      
        V_STATUS := 3;
      
        baf_mail_send(p_sender     => v_sender_add,
                      p_recipients => p_user_email,
                      p_subject    => v_sub,
                      p_message    => MYDATA);
        v_status_out := 'Y';
      EXCEPTION
        WHEN OTHERS THEN
          ROLLBACK;
          V_ERRMSG := SUBSTR(SQLERRM, 1, 250);
          INSERT INTO XXBAF_ERR_DATA_TRANSFER
            (ERR_PROC, ERR_MSG, ERR_DATE, ERR_STATUS)
          VALUES
            (V_PROC, V_ERRMSG, SYSDATE, V_STATUS);
          COMMIT;
      end;
    
    else
      v_status_out := 'N';
    
    END IF;
  
  end if;

EXCEPTION
  WHEN OTHERS THEN
    ROLLBACK;
    V_ERRMSG := SUBSTR(SQLERRM, 1, 250);
    INSERT INTO XXBAF_ERR_DATA_TRANSFER
      (ERR_PROC, ERR_MSG, ERR_DATE, ERR_STATUS)
    VALUES
      (V_PROC, V_ERRMSG, SYSDATE, V_STATUS);
    COMMIT;
