  v_attr3        varchar2(200);
  v_attr4        varchar2(200);
  v_attr5        varchar2(200);
  v_attr6        varchar2(200);
  v_attr7        varchar2(200);
  v_attr8        varchar2(200);
  v_attr9        varchar2(200);
  v_attr10       varchar2(200);
  v_currency     varchar2(200);
  v_amt          varchar2(200);
  v_frm_dt       varchar2(200);
  v_to_dt        varchar2(200);

  LINE  TABLE_TYPES1.TSTRING;
  FIELD TABLE_TYPES1.TSTRING;
BEGIN
  V_STATUS := 1;
  V_PROC   := 'XXBAF_UPDATE_MASTER_EXPENSE';

  --select xxbaf_master_exp_seq.nextval into master_exp_seq from dual;

  LINE := XXBAF_SPLIT1(v_totStr, '@@');

  FOR I IN 1 .. LINE.COUNT LOOP
    V_ERRMSG := NULL;

    FIELD := XXBAF_SPLIT1(LINE(I), '~');

    master_exp_seq:= FIELD(1);
    line_exp_seq:= FIELD(2);
    v_empgrd   := FIELD(3);
    v_expcat   := FIELD(4);
    v_attr1    := FIELD(5);
    v_attr2    := FIELD(6);
    v_attr3    := FIELD(7);
    v_attr4    := FIELD(8);
    v_attr5    := FIELD(9);
    v_attr6    := FIELD(10);
    v_attr7    := FIELD(11);
    v_attr8    := FIELD(12);
    v_attr9    := FIELD(13);
    v_attr10   := FIELD(14);
    v_currency := FIELD(15);
    v_amt      := FIELD(16);
    v_frm_dt   := FIELD(17);

    IF (v_frm_dt = '-') THEN
      v_frm_dt := '';
    end IF;

    v_to_dt := FIELD(18);
    IF (v_to_dt = '-') THEN
      v_to_dt := '';
    end IF;

    update xxbaf_expense_master set
       empgrade=v_empgrd,
       expense_cat=v_expcat,
       attribute1=v_attr1,
       attribute2=v_attr2,
       attribute3=v_attr3,
       attribute4=v_attr4,
       attribute5=v_attr5,
       attribute6=v_attr6,
       attribute7=v_attr7,
       attribute8=v_attr8,
       attribute9=v_attr9,
       attribute10=v_attr10,
       currency=v_currency,
       amount=v_amt,
       fromdt=v_frm_dt,
       todt=v_to_dt,
       updated_by=v_user,
       updated_on=sysdate
    where master_seq=master_exp_seq
     and  line_seq=line_exp_seq;

  end loop;

  v_status_out := 'Data Updated Successfully';
EXCEPTION
  WHEN OTHERS THEN
    ROLLBACK;
    V_ERRMSG := SUBSTR(SQLERRM, 1, 250);
    INSERT INTO XXBAF_ERR_DATA_TRANSFER
      (ERR_PROC, ERR_MSG, ERR_DATE, ERR_STATUS)
    VALUES
      (V_PROC, V_ERRMSG, SYSDATE, V_STATUS);
    COMMIT;
    v_status_out := 'Some Error Occurred';
end;

