                'dbms_sql.close_cursor(l_c); end;';
   dbms_sql.parse(c,statement,dbms_sql.native);

   dbms_sql.bind_variable(c, ':lb_bind', lb);
   dbms_sql.bind_variable(c, ':ub_bind', ub);

   rows_processed := dbms_sql.execute(c);
