  CREATE OR REPLACE EDITIONABLE FUNCTION "XXBAF"."XXBAF_SPLIT" (ST IN VARCHAR2, SPLITTER VARCHAR2) RETURN	Table_Types.Tstring AS
I   INTEGER:=0;
X INTEGER:=1;
Y INTEGER:=0;
S VARCHAR2(500);
ARR Table_Types.TSTRING;

BEGIN
WHILE X <= LENGTH(ST)
LOOP
	  Y := INSTR(ST, SPLITTER, X);
	  IF Y >0 THEN
	  	 				S:=SUBSTR(ST,X,Y-X);
						X:=Y+LENGTH(SPLITTER);
	  ELSE
	  	  				S:=SUBSTR(ST,X,LENGTH(ST));
	  END IF;

	  I:=I+1;

	  ARR(I):=S;
	  IF Y=0 THEN
	  	 	 	  EXIT;
	 END IF;
END LOOP;

RETURN (ARR);

END;

